import { put, takeLatest, all, call } from "redux-saga/effects";
import { server } from "../server";

const apiUrl = "http://tpi.uneed.ir:7100/post/search";
var myHeaders = new Headers();
myHeaders.append("t", "WFT9KbF8mCoP6cKFWCmfqvs/pU9TUR8IRLOqMgOFpNE=");
myHeaders.append("Content-Type", "application/json");

var raw = JSON.stringify({
  lm: "2",
  pg: "2",
  tp: "1",
});

async function getApi() {
  const response = await fetch(apiUrl, {
    method: "POST",
    headers: myHeaders,
    body: raw,
    redirect: "follow",
  });
  const result_1 = await response.text();
  return JSON.parse(result_1);
}

function* fetchNews() {
  try {
    const products = yield call(getApi);
    console.log(products);
    yield put({ type: "NEWS_RECEIVED", product: products });
  } catch (e) {
    yield put({ type: "GET_USERS_FAILED", message: e.message });
  }
}

function* actionWatcher() {
  yield takeLatest("GET_NEWS", fetchNews);
}

export default function* rootSaga() {
  yield all([actionWatcher()]);
}
