import { useRef, useEffect } from "react";
import { connect } from "react-redux";
import { getNews } from "../../actions";
import styled from "styled-components";
const Product = (props) => {
  //   const mounted = useRef();
  //   useEffect(() => {
  //     if (!mounted.current) {
  //     }
  //     mounted.current = true;
  //   });

  const PRODUCTS = styled.div`
    display: flex;
    justify-content: space-evenly;
    p {
      margin: 0;
    }
    .store-detail p {
      padding-right: 0.25rem;
    }
    .head,
    .product,
    .body,
    .footer {
      padding: 0.25rem;
    }
    .product {
      box-shadow: rgba(99, 99, 99, 0.2) 0px 2px 8px 0px;
      border-radius: 8px;
      width: 20rem;
    }
    .head {
      display: flex;
      align-items: center;
      justify-content: flex-end;
      padding: 0.5rem;
    }
    .body {
      display: flex;
      flex-direction: column;
      align-items: center;
    }
    .product-image img {
      border-radius: 10px;
    }
    .product-detail {
      display: flex;
      flex-direction: column;
      align-items: flex-end;
      text-align: end;
    }
    .product-action {
      display: flex;
      justify-content: space-between;
      flex-direction: row;
      width: 100%;
      font-size: larger;
      padding: 0.25rem 0.75rem;
    }
    .store-detail {
      text-align: end;
    }
    .store-place {
      display: flex;
      align-items: center;
    }
    .store-name {
      display: flex;
      justify-content: flex-end;
    }
  `;

  let imageurl = "http://tpi.uneed.ir:7100/m/";
  //   let hi = 1;

  console.log(props.product.map((item) => item.post));
  const displayProduct = () => {
    return props.product.map((item) => {
      return (
        <div className="product">
          <div className="head">
            <div className="store-detail">
              <div className="store-name">
                <p>{item.post.business.name}</p>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  height="20"
                  viewBox="0 0 24 24"
                  width="18"
                  color="blue"
                >
                  <path
                    d="m9.00012 12 1.99998 2 4-4m-7.16524-5.30295c.71753-.05726 1.39872-.33942 1.94658-.8063 1.27846-1.08952 3.15886-1.08952 4.43736 0 .5479.46688 1.229.74904 1.9466.8063 1.6744.13362 3.0041 1.46326 3.1377 3.13769.0572.71753.3394 1.39872.8063 1.94658 1.0895 1.27848 1.0895 3.15888 0 4.43738-.4669.5478-.7491 1.229-.8063 1.9466-.1336 1.6744-1.4633 3.004-3.1377 3.1377-.7175.0572-1.3987.3394-1.9466.8063-1.2785 1.0895-3.1589 1.0895-4.43736 0-.54786-.4669-1.22905-.7491-1.94658-.8063-1.67443-.1337-3.00407-1.4633-3.13769-3.1377-.05726-.7176-.33942-1.3988-.8063-1.9466-1.08952-1.2785-1.08952-3.1589 0-4.43738.46688-.54786.74904-1.22905.8063-1.94658.13362-1.67443 1.46326-3.00407 3.13769-3.13769z"
                    stroke="#4a5568"
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    stroke-width="2"
                  />
                </svg>
              </div>
              <div className="store-place">
                <p>{item.post.regionName}</p>
                <ion-icon name="location-outline"></ion-icon>
              </div>
            </div>
            <div className="store-image">
              <img
                src={imageurl + item.post.business.avatar.hash}
                width="50"
                height="50"
              />
            </div>
          </div>
          <div className="body">
            <div className="product-image">
              <img
                src={imageurl + item.post.media.map((item) => item.hash)}
                width="300"
                height="200"
              />
            </div>
            <div className="product-action">
              <div className="left-icon">
                <ion-icon name="chatbubble-ellipses-outline"></ion-icon>
                <ion-icon name="call-outline"></ion-icon>
              </div>
              <div className="right-icon">
                <ion-icon name="share-social-outline"></ion-icon>
                <ion-icon name="bookmark-outline"></ion-icon>
                <ion-icon name="heart-outline"></ion-icon>
              </div>
            </div>
          </div>
          <div className="footer">
            <div className="product-detail">
              <div className="product-title">{item.post.service.name}</div>
              <div className="product-about">
                {item.post.service.description}
              </div>
            </div>
            <div product="product-price">
              <div className="price">515</div>
              <div className="minute">515</div>
            </div>
          </div>
        </div>
      );
    });
  };
  return (
    <PRODUCTS className="products">
      {/* <div>hi</div> */}
      <div>hell</div>
      {props.product ? displayProduct() : <div>loading</div>}
    </PRODUCTS>
  );
};

const mapStateToProps = (state) => {
  return { product: state.product };
};

export default connect(mapStateToProps, { getNews })(Product);
