import { useRef, useEffect } from "react";
import { connect } from "react-redux";
import { getNews } from "../../actions";
import styled from "styled-components";
const Product = (props) => {
  //   const mounted = useRef();
  //   useEffect(() => {
  //     if (!mounted.current) {
  //     }
  //     mounted.current = true;
  //   });

  const PRODUCTS = styled.div`
    display: flex;
    justify-content: space-around;
    .head {
      display: flex;
      align-items: center;
    }
  `;

  console.log(props.product.map((item) => item.post));
  const displayProduct = () => {
    if (props.product) {
      return props.product.map((item) => {
        return (
          <div className="product">
            <div className="head">
              <div className="store-detail">
                <div className="store-name">{item.post.business.name}</div>
                <div className="store-place">{item.post.regionName}</div>
              </div>
              <div className="store-image">avatar</div>
            </div>
            <div className="body">
              <div className="product-image">image</div>
              <div className="product-action">icon</div>
            </div>
            <div className="footer">
              <div className="product-detail">
                <div className="product-title">new</div>
                <div className="product-about">new new</div>
              </div>
              <div product="product-price">
                <div className="price">515</div>
                <div className="minute">515</div>
              </div>
            </div>
          </div>
        );
      });
    } else if (!props.product) {
      return <div>loading</div>;
    }
  };

  return (
    <PRODUCTS className="products">
      <div>hi</div>
      <div>hello</div>
      {displayProduct()}
    </PRODUCTS>
  );
};

const mapStateToProps = (state) => {
  return { product: state.product };
};

export default connect(mapStateToProps, { getNews })(Product);
