import { useRef, useEffect } from "react";
import { connect } from "react-redux";
import { getNews } from "../../actions";
import styled from "styled-components";
const Product = (props) => {
  //   const mounted = useRef();
  //   useEffect(() => {
  //     if (!mounted.current) {
  //     }
  //     mounted.current = true;
  //   });

  console.log(props.product);

  const PRODUCT = styled.div`
    .head {
      display: flex;
      align-items: center;
    }
  `;

  return (
    <PRODUCT>
      <div className="head">
        <div className="store-detail">
          <div className="store-name">name</div>
          <div className="store-place">palce</div>
        </div>
        <div className="store-image">avatar</div>
      </div>
      <div className="body">
        <div className="product-image">image</div>
        <div className="product-action">icon</div>
      </div>
      <div className="footer">
        <div className="product-detail">
          <div className="product-title">new</div>
          <div className="product-about">new new</div>
        </div>
        <div product="product-price">
          <div className="price">515</div>
          <div className="minute">515</div>
        </div>
      </div>
    </PRODUCT>
  );
};

const mapStateToProps = (state) => {
  return { product: state.product };
};

export default connect(mapStateToProps, { getNews })(Product);
