import { put, takeLatest, all } from "redux-saga/effects";
import { server } from "../server";

function* fetchNews() {
  var myHeaders = new Headers();
  myHeaders.append("t", "WFT9KbF8mCoP6cKFWCmfqvs/pU9TUR8IRLOqMgOFpNE=");
  myHeaders.append("Content-Type", "application/json");

  var raw = JSON.stringify({
    lm: "2",
    pg: "2",
    tp: "1",
  });

  var requestOptions = {
    method: "POST",
    headers: myHeaders,
    body: raw,
    redirect: "follow",
  };

  let productData = null;

  const response = fetch("http://tpi.uneed.ir:7100/post/search", requestOptions)
    .then((response) => response.text())
    .then((result) => {
      productData = JSON.parse(result);
    });
  yield put({ type: "NEWS_RECEIVED", product: productData });
}

function* actionWatcher() {
  yield takeLatest("GET_NEWS", fetchNews);
}

export default function* rootSaga() {
  yield all([actionWatcher()]);
}
